@use 'sass/forward' as *;

// estilos del carrusel
@import "node_modules/@glidejs/glide/src/assets/sass/glide.core";
// fuentes
@import url("https://fonts.googleapis.com/css2?family=Raleway:wght@300;600&display=swap");

@import url("https://fonts.googleapis.com/css2?family=Oxygen:wght@300;400;700&family=Raleway:wght@300;600&display=swap");

html {
  background-color: #ffffff;
  font-size: clamp(1.1rem, 0.8rem, 4vw);
  scroll-behavior: smooth;
}

body {
  font-family: Oxygen;
  font-weight: 300;
  position: relative;
}
h1,
h2,
h3,
h4 {
  font-family: Raleway;
  font-weight: 300;
  b {
    font-weight: 600;
  }
}

.h2sepa {
  font-family: Oxygen;
  font-size: 1vw;
  @include cel{
    font-size: clamp(1.6vh, 0.6vw, 2vw);
  }
  letter-spacing: 0.2rem;
  text-align: center;
}
h2 {
  font-size: clamp(4.5vh, 3vw, 5vw);
}
.paddArriba {
  padding-top: 5rem;
}
.paddAbajo {
  padding-bottom: 3rem;
}

.minmax {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(50px, 1fr));
  // grid-template-columns: repeat(8, 1fr);
  margin-bottom: 9rem;
  @include cel {
    margin-bottom: 0rem;
  }
}

.conte {
  max-width: 1920px;
  margin: 0 auto;
 padding: 0 5vw;

}

.porqueHome {
  background: url("/img/Porque-home-icza.jpg") no-repeat;
  background-size: cover;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  color: #ffffff;
  grid-column: 1/2;
  position: relative;
  img {
    position: absolute;
    bottom: -28%;
    left: 20%;
  }
  @include cel {
    grid-column: 1/8;
    height: 30vh;
    img {
      width: 25%;
      bottom: -18%;
      left: 0;
    }
  }

  @include hd {
    height: 25vw;
  }
}

.bandejaTxt {
  display: flex;
  padding: 3em 0;
  justify-content: center;
  align-items: center;
  div {
    width: 60%;
    @include cel {
      width: 70%;
    }
  }

  li {
    margin-left: 1.1rem;
  }
  h3,
  li,
  p {
    margin-bottom: 2em;
  }
  p:last-child {
    margin-bottom: 0em;
  }
  li:last-child {
    margin-bottom: 0em;
  }

  grid-column: 0/-1;
  @include cel {
    grid-column: 1/-1;
  }
  @include cel {
    order: 1;
    margin-top: 2rem;
  }
}

#consultoria {
  h2 {
    text-align: center;
    @include cel {
      padding: 1em 2em 0;
    }
  }
}
#caso {
  position: relative;
  background-color: $azulMuyClaro;
  background: linear-gradient(90deg, #ffffff 25%, $azulMuyClaro 25%);
  @include cel {
    background: $azulMuyClaro;
    margin-bottom: 5rem;
  }
  h2 {
    text-align: left;
    padding-bottom: 1rem;
  }
  p {
    margin-bottom: 0.6rem;
  }
}
#conteImaCaso {
  position: relative;
  transform: translateY(-3rem);
  img {
    width: 100%;
    height: auto;
    object-fit: cover;
  }
  @include cel {
    grid-column: 1/-1;
    transform: translateY(0rem);
    height: 70vh;
    img {
      height: 100%;
    }
  }
}
#jorge {
  position: absolute;
  right: 0;
  bottom: 5px;
  background-color: $azulOscuro;
  color: #ffffff;
  padding: 2.5rem;
  width: 50%;
  opacity: 0.9;
  @include cel {
    width: 100%;
    bottom: 0;
  }
}

#gridlogos{
  position: relative;
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  @include cel{
    grid-template-columns: repeat(3, 1fr);
  }
  div{
    display: flex;
    padding:1rem;
    justify-content: center;
    align-items: center;

  }

}
#metodologia{
  padding: 5rem 0;
  background: url('img/fondo-metodologia-icza-way.jpg') no-repeat;
  background-size: 100%;
  background-position: center bottom;
  @include cel{
    background-size: 500%;
    h2{
      text-align: left;
    }
    .h2sepa{
      padding-left:3rem;
    }
  }
  .iczaWay{
    display: flex;
    justify-content: center;
    padding-top: 3rem;
    @include cel{
      grid-column: 2/-1;
      justify-content: left;
      padding-top: 1rem;
    }

  }
  .bandejaTxt{
    div{
      width: 80%;
    }
    justify-content: left;
    @include cel{
      padding-top:0;
      div{
        width: 70%;
      }justify-content: center;    
    .btnAzul{
      text-align: center;
    }
    }
  }
}

#footer {
  h3 {
    text-align: left;
    @include cel{
      text-align: center;
    }
  }
  p {
    font-size: 0.9rem;
    padding-top: 1rem;
  }
  #social {
    display: flex;
    li a {
      display: block;
      padding: 1rem 1rem 0 0;
    }
  }
  .minmax {
    margin-bottom: 5rem;
    @include cel{
      div{
        grid-column: 1/-1;
        text-align: center;
        margin-top: 1.5rem;
      }
      #logoPie{
        margin-top: 6rem;
        @include cel{
          margin-top: 0rem;
        }
      }
    }
    div + div {
      display: flex;
      justify-content: center;
    }
  }
  #legales {
    margin-bottom: 2rem;
    border-top: solid 1px $azulOscuro;
    display: flex;
    justify-content: space-between;
    @include cel{
      display: block;
      text-align: center;
    }
  }
}

